import { getInitialDataMask } from './reducer';
export const CLEAR_DATA_MASK_STATE = 'CLEAR_DATA_MASK_STATE';
export const UPDATE_DATA_MASK = 'UPDATE_DATA_MASK';
export const INIT_DATAMASK = 'INIT_DATAMASK';
export const SET_DATA_MASK_FOR_FILTER_CHANGES_COMPLETE = 'SET_DATA_MASK_FOR_FILTER_CHANGES_COMPLETE';
export function setDataMaskForFilterChangesComplete(filterChanges, filters) {
  return {
    type: SET_DATA_MASK_FOR_FILTER_CHANGES_COMPLETE,
    filterChanges,
    filters
  };
}
export function updateDataMask(filterId, dataMask) {
  return {
    type: UPDATE_DATA_MASK,
    filterId,
    dataMask
  };
}
export function clearDataMask(filterId) {
  return updateDataMask(filterId, getInitialDataMask(filterId));
}
export function clearDataMaskState() {
  return {
    type: CLEAR_DATA_MASK_STATE
  };
}
