import dayjs from 'dayjs';
import utc from 'dayjs/plugin/utc';
import { getTimeFormatter, getTimeFormatterForGranularity, SMART_DATE_ID } from '@superset-ui/core';
dayjs.extend(utc);
export const parseMetricValue = (metricValue) => {
  if (typeof metricValue === 'string') {
    const dateObject = dayjs.utc(metricValue, undefined, true);
    if (dateObject.isValid()) {
      return dateObject.valueOf();
    }
    return null;
  }
  return metricValue;
};
export const getDateFormatter = (timeFormat, granularity, fallbackFormat) => timeFormat === SMART_DATE_ID ?
getTimeFormatterForGranularity(granularity) :
getTimeFormatter(timeFormat != null ? timeFormat : fallbackFormat);
