// Using bem standard
export const testWithId = (prefix, idOnly = false) => (id, localIdOnly = false) => {
  const resultIdOnly = localIdOnly || idOnly;
  if (!id && prefix) {
    return resultIdOnly ? prefix : {};
  }
  if (id && !prefix) {
    return resultIdOnly ? id : {};
  }
  if (!id && !prefix) {
    console.warn('testWithId function has missed "prefix" and "id" params');
    return resultIdOnly ? '' : {};
  }
  const newId = `${prefix}__${id}`;
  return resultIdOnly ? newId : {};
};
